import React, { Component } from "react";
import {
  DistanceMatrixService,
  LoadScript,
  Autocomplete,
  DirectionsService,
  DirectionsRenderer,
  GoogleMap,
  MarkerF,
} from "@react-google-maps/api";

class Electricity extends Component {
  render() {
    return (
      <Box
        className="App"
        sx={{ pt: 7, flexGrow: 1, px: 5, height: `100%`, width: `100%` }}
      >
        <Grid container direction="row" spacing={1}>
          <Grid
            item
            direction="column"
            justifyContent="flex-start"
            alignItems="flex-start"
            alignspacing={1}
            xs={4}
            sx={{ px: 1 }}
          >
            <Grid item sx={{ py: 1 }}>
              <LoadScript
                googleMapsApiKey="AIzaSyBKHL2R02wDBkDHAWv9b-mnaHmu6MmaWBc"
                libraries={["places"]}
              >
                <AutocompleteApiComponent />
              </LoadScript>
            </Grid>
          </Grid>
        </Grid>
      </Box>
    );
  }
}

// handles map display and travel input

class AutocompleteApiComponent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      origin: null,
    };

    this.origin_autocomplete = null;

    this.origin_onLoad = this.origin_onLoad.bind(this);
    this.origin_onPlaceChanged = this.origin_onPlaceChanged.bind(this);
  }

  // called on origin textbox load

  origin_onLoad(origin_autocomplete) {
    console.log("origin autocomplete: ", origin_autocomplete);

    this.origin_autocomplete = origin_autocomplete;
  }

  // called on destination textbox load

  origin_onPlaceChanged() {
    if (this.origin_autocomplete != null) {
      this.state.origin = this.origin_autocomplete.getPlace();
      this.setState({ origin: this.origin_autocomplete.getPlace() });
      console.log(this.state.origin);
    } else {
      console.log("Autocomplete is not loaded yet!");
    }
  }

  // updates state when destination input is given

  render() {
    let position = { lat: 3, lng: 5 };
    if (this.state.origin !== null) {
      position = {
        lat: this.state.origin.geometry.viewport.Ab.hi,
        lng: this.state.origin.geometry.viewport.Va.hi,
      };
    }
    return (
      <div>
        <GoogleMap
          id="searchbox-example"
          mapContainerStyle={{ height: "600px", width: "800px" }}
          zoom={2.5}
          center={{ lat: 38.685, lng: -115.234 }}
        >
          {this.state.origin !== null && <MarkerF position={position} />}

          <Autocomplete
            onLoad={this.origin_onLoad}
            onPlaceChanged={this.origin_onPlaceChanged}
          >
            <input
              type="text"
              placeholder="Enter your address "
              style={{
                boxSizing: `border-box`,
                border: `1px solid transparent`,
                width: `240px`,
                height: `32px`,
                padding: `0 12px`,
                borderRadius: `3px`,
                boxShadow: `0 2px 6px rgba(0, 0, 0, 0.3)`,
                fontSize: `14px`,
                outline: `none`,
                textOverflow: `ellipses`,
                position: "absolute",
                left: "50%",
                top: "2%",
                marginLeft: "-120px",
              }}
            />
          </Autocomplete>
        </GoogleMap>
      </div>
    );
  }
}

export default Electricity;
